list.map:
function(doc) {
if(doc.type === 'document' && doc.published === true)	
  emit(doc._id, {title: doc.title, _id:doc._id, _rev:doc._rev, processed: doc.processed || false});
}

uploads.map:
function(doc) {
if(doc.type === 'document' && !doc.published)	
  emit(doc.user._id, {title: doc.title, _id:doc._id, _rev:doc._rev, creationDate: doc.creationDate, processed: doc.processed, uploaded: doc.uploaded || false, metadata:doc.metadata, template:doc.template});
}

metadata_by_template.map:
function(doc) {
  if(doc.type === 'document' && doc.template) {
    emit(doc.template, {_id: doc._id, metadata: doc.metadata});
  }
}
